/**
 * Type representing audio settings.
 */
type AudioSettings = {
    /**
     * Indicates whether audio is enabled.
     */
    enabled: boolean;
    /**
     * Indicates whether audio is earmuffed.
     */
    earmuffed: boolean;
    /**
     * Indicates whether audio is muted.
     */
    muted: boolean;
};

export type { AudioSettings };
