import { AbstractAudioMessage } from '../AbstractAudioMessage.js';
import { MessengerAudioParams } from '../../types/Channels/Messenger/MessengerAudioParams.js';
import { Channels } from '../../enums/Channels.js';
import { MessengerType } from '../../types/Channels/Messenger/MessengerType.js';
import '../AbstractMessage.js';
import '../../enums/MessageTypes.js';
import '../../types/MessageParams.js';
import '../../types/MessageParamsAudio.js';
import '../../types/MessageAudioType.js';
import '../../types/Channels/Messenger/MessengerParams.js';
import '../../enums/Messenger/MessengerCategory.js';

/**
 * Represents an audio message for the Messenger channel.
 *
 * @group Messenger
 */
declare class MessengerAudio extends AbstractAudioMessage implements MessengerAudioParams {
    /**
     * The channel for this message (always 'messenger').
     */
    channel: Channels.MESSENGER;
    /**
     * The messenger information for this message.
     */
    messenger: MessengerType;
    /**
     * Sends an audio message to the Facebook Messenger channel.
     *
     * @param {MessengerAudioParams} params - The parameters for creating a Messenger audio message.
     * @example
     * ```ts
     * import { MessengerAudio } from '@vonage/messages';
     *
     * const { messageUUID } = await messagesClient.send(new MessengerAudio({
     *  to: TO_NUMBER,
     *  from: FROM_NUMBER,
     *  audio: {
     *    url: 'https://example.com/audio.mp3',
     *  },
     *  clientRef: 'my-personal-reference',
     * }));
     *
     * console.log(`Message sent successfully with UUID ${messageUUID}`);
     * ```
     */
    constructor(params: Omit<MessengerAudioParams, 'channel' | 'messageType'>);
}

export { MessengerAudio };
