import { MessageParams } from '../../types/MessageParams.js';
import { MessageTypes } from '../../enums/MessageTypes.js';
import { Channels } from '../../enums/Channels.js';
import { MessengerType } from '../../types/Channels/Messenger/MessengerType.js';
import { MessengerFileParams } from '../../types/Channels/Messenger/MessengerFileParams.js';
import { AbstractFileMessage } from '../AbstractFileMessage.js';
import '../../enums/Messenger/MessengerCategory.js';
import '../../types/MessageFileType.js';
import '../../types/MessageParamsFile.js';
import '../../types/Channels/Messenger/MessengerParams.js';
import '../AbstractMessage.js';

/**
 * Represents a file message for the Messenger channel.
 *
 * @group Messenger
 */
declare class MessengerFile extends AbstractFileMessage implements MessengerFileParams, MessageParams {
    /**
     * The channel for this message (always 'messenger').
     */
    channel: Channels.MESSENGER;
    /**
     * The type of message (always 'file').
     */
    messageType: MessageTypes.FILE;
    /**
     * The messenger information for this message.
     */
    messenger: MessengerType;
    /**
     * Sends a file message to the Facebook Messenger channel.
     *
     * @param {MessengerFileParams} params - The parameters for creating a Messenger file message.
     *
     * @example
     * ```ts
     * import { MessengerFile } from '@vonage/messages';
     *
     * const { messageUUID } = await messagesClient.send(new MessengerFile({
     *  to: TO_NUMBER,
     *  from: FROM_NUMBER,
     *  file: {
     *    url: 'https://example.com/image.jpg',
     *    caption: 'This is an image',
     *  },
     *  clientRef: 'my-personal-reference',
     * }));
     *
     * console.log(`Message sent successfully with UUID ${messageUUID}`);
     * ```
     */
    constructor(params: Omit<MessageParams, 'channel' | 'messageType'> & Omit<MessengerFileParams, 'channel' | 'messageType'>);
}

export { MessengerFile };
