import { AbstractFileMessage } from '../AbstractFileMessage.js';
import { Channels } from '../../enums/Channels.js';
import { ViberFileParams } from '../../types/Channels/Viber/ViberFileParams.js';
import '../AbstractMessage.js';
import '../../enums/MessageTypes.js';
import '../../types/MessageParams.js';
import '../../types/MessageFileType.js';
import '../../types/MessageParamsFile.js';
import '../../types/Channels/Viber/ViberParams.js';

/**
 * Represents a file message for the Viber Service channel.
 *
 * @group Viber
 */
declare class ViberFile extends AbstractFileMessage implements ViberFileParams {
    /**
     * The channel for this message (always 'viber_service').
     */
    channel: Channels.VIBER;
    /**
     * Send a file message using the Viber Service channel.
     *
     * @param {ViberFileParams} params - The parameters for the ViberFile message.
     * @example
     * ```ts
     * import { ViberFile } from '@vonage/messages';
     *
     * const { messageUUID } = await messagesClient.send(new ViberFile({
     *  to: TO_NUMBER,
     *  from: FROM_NUMBER,
     *  file: {
     *    url: 'https://my-host.com/my-file.pdf',
     *  },
     *  viberService: {
     *    action: {
     *      url: 'https://my-host.com/my-path',
     *      text: 'My button text',
     *    },
     *  },
     *  clientRef: 'my-personal-reference',
     * }));
     *
     * console.log(`Message sent successfully with UUID ${messageUUID}`);
     * ```
     */
    constructor(params: Omit<ViberFileParams, 'channel' | 'messageType'>);
}

export { ViberFile };
