import { Channels } from '../../enums/Channels.js';
import { AbstractFileMessage } from '../AbstractFileMessage.js';
import { WhatsAppContext } from '../../types/Channels/WhatsApp/WhatsAppParams.js';
import { WhatsAppFileParams } from '../../types/Channels/WhatsApp/WhatsAppFileParams.js';
import '../AbstractMessage.js';
import '../../enums/MessageTypes.js';
import '../../types/MessageParams.js';
import '../../types/MessageFileType.js';
import '../../types/MessageParamsFile.js';

/**
 * Represents a file message for WhatsApp.
 *
 * @group WhatsApp
 */
declare class WhatsAppFile extends AbstractFileMessage implements WhatsAppFileParams {
    /**
     * The channel for this message (always 'whatsapp').
     */
    channel: Channels.WHATSAPP;
    context?: WhatsAppContext;
    /**
     * Send a WhatsApp file message.
     *
     * @param {WhatsAppFileParams} params - The parameters for creating a WhatsApp file message.
     * @example
     * ```ts
     * import { WhatsAppFile } from '@vonage/messages';
     *
     * const { messageUUID } = await messagesClient.send(new WhatsAppFile({
     *  to: TO_NUMBER,
     *  from: FROM_NUMBER,
     *  file: {
     *    url: 'https://example.com/image.jpg',
     *  },
     *  clientRef: 'my-personal-reference',
     * }));
     *
     * console.log(`Message sent successfully with UUID ${messageUUID}`);
     * ```
     */
    constructor(params: Omit<WhatsAppFileParams, 'channel' | 'messageType'>);
}

export { WhatsAppFile };
