import { VetchOptions } from './vetchOptions.js';
import { Headers } from './headers.js';
import '../enums/HTTPMethods.js';
import '../enums/contentType.js';
import '../enums/responseTypes.js';

/**
 * Represents the response returned by a Vetch request.
 *
 * @template T - The type of the response data.
 */
type VetchResponse<T> = {
    /**
     * The configuration options used for the request.
     */
    config: VetchOptions;
    /**
     * The parsed response data.
     */
    data: T;
    /**
     * The response headers.
     */
    headers: Headers;
    /**
     * The HTTP status code of the response.
     */
    status: number;
    /**
     * The HTTP status text of the response.
     */
    statusText: string;
    /**
     * The configuration options for the request (same as 'config').
     */
    request: VetchOptions;
};

export type { VetchResponse };
