import { PhoneEndpoint } from '../Endpoint/PhoneEndpoint.js';
import { SIPEndpoint } from '../Endpoint/SIPEndpoint.js';
import { VBCEndpoint } from '../Endpoint/VBCEndpoint.js';
import { WebsocketEndpoint } from '../Endpoint/WebsocketEndpoint.js';

/**
 * Represents the response for a call endpoint, which can be one of the following:
 * - Phone endpoint
 * - Websocket endpoint
 * - SIP endpoint
 * - VBC (Vonage Business Cloud) endpoint
 *
 * @remarks
 * Vonage API's will return information using `snake_case`. This represents the
 * pure response before the client will transform the keys into `camelCase`.
 */
type CallEndpointResponse = PhoneEndpoint | WebsocketEndpoint | SIPEndpoint | VBCEndpoint;

export type { CallEndpointResponse };
